package com.weibin.javabean;

public class ShoppingCategory {
	private int category_id;
	private String category_name;
	private String category_description;
	private int category_image_id;
	public ShoppingCategory() {
		super();
		// TODO Auto-generated constructor stub
	}
	public ShoppingCategory(int category_id, String category_name,
			String category_description, int category_image_id) {
		super();
		this.category_id = category_id;
		this.category_name = category_name;
		this.category_description = category_description;
		this.category_image_id = category_image_id;
	}
	public int getCategory_id() {
		return category_id;
	}
	public void setCategory_id(int category_id) {
		this.category_id = category_id;
	}
	public String getCategory_name() {
		return category_name;
	}
	public void setCategory_name(String category_name) {
		this.category_name = category_name;
	}
	public String getCategory_description() {
		return category_description;
	}
	public void setCategory_description(String category_description) {
		this.category_description = category_description;
	}
	public int getCategory_image_id() {
		return category_image_id;
	}
	public void setCategory_image_id(int category_image_id) {
		this.category_image_id = category_image_id;
	}
	@Override
	public String toString() {
		return "ShoppingCategory [category_id=" + category_id
				+ ", category_name=" + category_name
				+ ", category_description=" + category_description
				+ ", category_image_id=" + category_image_id + "]";
	}

}
